mapper.update:
UPDATE member
SET member_name=#{name}, member_id=#{id}, member_phonenum=#{phonenum}, member_email=#{email}
WHERE member_id=#{member_id}

mapper.select:
SELECT shop_name, shop_tel, shop_loc, shop_notice, shop_opentime, shop_closetime
FROM shop
WHERE member_id=#{member_id}

mapper.update:
UPDATE shop
SET shop_name=#{name}, shop_tel=#{tel}, shop_loc=#{loc}, shop_notice=#{notice}, shop_opentime=#{opentime}, shop_closetime=#{closetime}
WHERE member_id=#{member_id} AND

mapper.select:
SELECT * FROM reservation 
WHERE member_name=(SELECT member_name FROM member where member_id=#{id}) 
ORDER BY reservation_no DESC LIMIT #{startPage}, #{lastPage}

mapper.update:
UPDATE member
SET member_pw=HEX(AES_ENCRYPT(#{pw1}, 'teamc'))
WHERE member_id=#{member_id}

mapper.select:
SELECT s.*, l.member_no FROM shop s JOIN shop_like l ON s.shop_no = l.shop_no 
WHERE l.member_no=(SELECT member_no FROM member where member_id=#{id}) 
ORDER BY l.shop_no DESC LIMIT #{startPage}, #{lastPage}

mapper.update:
UPDATE shop 
SET shop_name=#{shop_name}, shop_tel=#{shop_tel}, shop_loc=#{shop_loc}, shop_gu=#{shop_gu}, shop_opentime=#{shop_opentime}, shop_closetime=#{shop_closetime}, shop_notice=#{shop_notice} 
WHERE member_no=(SELECT member_no FROM member where member_id=#{member_id})
